// koffee 1.20.0
var Kode, assert, chai, fs, kode, koffee, lexer, parser, path, renderer;

assert = require('assert');

chai = require('chai');

path = require('path');

fs = require('fs');

koffee = require('koffee');

Kode = require('../');

chai.should();

kode = new Kode();

lexer = kode.lexer;

parser = kode.parser;

renderer = kode.renderer;

describe('modules', function() {
    it('tokenize', function() {
        return lexer.tokenize('a=b').should.eql([
            {
                col: 0,
                line: 1,
                text: 'a',
                type: 'var'
            }, {
                col: 1,
                line: 1,
                text: '=',
                type: 'op'
            }, {
                col: 2,
                line: 1,
                text: 'b',
                type: 'var'
            }
        ]);
    });
    it('blockify', function() {
        return lexer.blockify(lexer.tokenize("if 1\n    a=b")).should.eql({
            type: 'block',
            tokens: [
                {
                    type: 'keyword',
                    text: 'if',
                    line: 1,
                    col: 0
                }, {
                    type: 'num',
                    text: '1',
                    line: 1,
                    col: 3
                }, {
                    type: 'block',
                    indent: '    ',
                    tokens: [
                        {
                            col: 4,
                            line: 2,
                            text: 'a',
                            type: 'var'
                        }, {
                            col: 5,
                            line: 2,
                            text: '=',
                            type: 'op'
                        }, {
                            col: 6,
                            line: 2,
                            text: 'b',
                            type: 'var'
                        }
                    ],
                    line: 2,
                    last: 2,
                    col: 4
                }
            ],
            indent: '',
            line: 1,
            last: 1,
            col: 0
        });
    });
    it('parse', function() {
        return parser.parse(lexer.blockify(lexer.tokenize("if a then b else c"))).should.eql([
            {
                "if": {
                    exp: {
                        col: 3,
                        line: 1,
                        text: "a",
                        type: "var"
                    },
                    "else": [
                        {
                            col: 17,
                            line: 1,
                            text: 'c',
                            type: 'var'
                        }
                    ],
                    then: [
                        {
                            col: 10,
                            line: 1,
                            text: 'b',
                            type: 'var'
                        }
                    ]
                }
            }
        ]);
    });
    return it('render', function() {
        return renderer.render(parser.parse(lexer.blockify(lexer.tokenize("if a then b else c")))).should.eql("if (a)\n{\n    b\n}\nelse\n{\n    c\n}");
    });
});

